import axios from 'axios';

export class HttpService {

    constructior() {
        this.backendPath = process.env.BACKEND_PATH;
        this.customersPath = "customers/"
    }

    signUp(signUpFormData, successCallback, errorCallback) {
        let url = this.backendPath + this.customersPath;
        axios
          .post(url, signUpFormData)
          .then(response => {
            successCallback(response.data);
          })
          .catch(error => {
              errorCallback(error);
          });
    }

    // TODO
    // logiIn

    getIngredients(callback) {
        axios.
            get(url, {
                headers: {
                    "Access-Control-Allow-Origin": "*"
                }
        })
          .then(response => {
              callback(response.data);
          })
          .catch(error => {
              console.log("Error occured ", error)
          })
    }

}
